import React, { useState } from "react";
import "./Calculadora.css";
import menu from "../assets/menu.png"
import { Link } from "react-router-dom";

const Calculadora = () => {
  const [displayValue, setDisplayValue] = useState("");
  const [operation, setOperation] = useState("");
  const [clearDisplay, setClearDisplay] = useState(true);

  const addDigit = (digit) => {
    setDisplayValue((prevValue) =>
      prevValue === "0" || clearDisplay ? digit : prevValue + digit
    );
    setClearDisplay(false);
  };

  const clearAll = () => {
    setDisplayValue("");
    setOperation("");
    setClearDisplay(true);
  };

  const clearLastEntry = () => {
    setDisplayValue((prevValue) => prevValue.slice(0, -1));
  };

  const addOperation = (op) => {
    if (op === "%") {
      calculatePercentage();
    } else {
      setOperation(op);
      setDisplayValue((prevValue) => prevValue + " " + op + " ");
      setClearDisplay(false);
    }
  };

  const calculatePercentage = () => {
    try {
      const value = parseFloat(displayValue);
      const result = value / 100;
      setDisplayValue(result.toString());
    } catch (error) {
      console.error("Calculation Error:", error);
      setDisplayValue("Error");
    }
    setOperation("");
    setClearDisplay(true);
  };

  const calculateResult = () => {
    try {
      const result = eval(displayValue);
      setDisplayValue(result.toString());
    } catch (error) {
      console.error("Calculation Error:", error);
      setDisplayValue("Error");
    }
    setOperation("");
    setClearDisplay(true);
  };

  const changeSign = () => {
    setDisplayValue((prevValue) => {
      const lastSpace = prevValue.lastIndexOf(" ");
      const lastValue = prevValue.slice(lastSpace + 1);
      const newLastValue = parseFloat(lastValue) * -1;
      const newValue =
        prevValue.slice(0, lastSpace + 1) + newLastValue.toString();
      return newValue;
    });
  };

  return (
    <div classNameName="container">
      <Link to="/" classNameName="btn-voltar">
        Voltar
      </Link>
      <div classNameName="div-test">
        <div classNameName="centralizar">
          <div classNameName="calculadora">
            <div classNameName="menu">
              <img src={menu} alt="menu" />
          </div>
            <div classNameName="display">{displayValue || "0"}</div>
            <div classNameName="linha">
              <button
                classNameName={
                  clearDisplay ? "operation" : "operation clean"
                }
                onClick={clearDisplay ? clearAll : clearLastEntry}
              >
                {clearDisplay ? "AC" : "C"}
              </button>
              <button classNameName="operation" onClick={changeSign}>
                ±
              </button>
              <button
                classNameName="operation"
                onClick={() => addOperation("%")}
              >
                %
              </button>
              <button
                classNameName="calculator"
                onClick={() => addOperation("/")}
              >
                ÷
              </button>
            </div>
            <div classNameName="linha">
              <button classNameName="number" onClick={() => addDigit("7")}>
                7
              </button>
              <button classNameName="number" onClick={() => addDigit("8")}>
                8
              </button>
              <button classNameNameName="number" onClick={() => addDigit("9")}>
                9
              </button>
              <button
                classNameName="calculator"
                onClick={() => addOperation("*")}
              >
                ×
              </button>
            </div>
            <div classNameName="linha">
              <button classNameName="number" onClick={() => addDigit("4")}>
                4
              </button>
              <button classNameName="number" onClick={() => addDigit("5")}>
                5
              </button>
              <button classNameName="number" onClick={() => addDigit("6")}>
                6
              </button>
              <button
                classNameName="calculator"
                onClick={() => addOperation("-")}
              >
                −
              </button>
            </div>
            <div classNameName="linha">
              <button classNameName="number" onClick={() => addDigit("1")}>
                1
              </button>
              <button classNameName="number" onClick={() => addDigit("2")}>
                2
              </button>
              <button classNameName="number" onClick={() => addDigit("3")}>
                3
              </button>
              <button
                classNameName="calculator"
                onClick={() => addOperation("+")}
              >
                +
              </button>
            </div>
            <div classNameName="linha">
              <button
                classNameName="number-zero"
                onClick={() => addDigit("0")}
              >
                0
              </button>
              <button classNameName="number" onClick={() => addDigit(".")}>
                .
              </button>
              <button classNameName="botao-igual" onClick={calculateResult}>
                =
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Calculadora;
