import { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Form() {
  const [answer, setAnswer] = useState('');
  const [error, setError] = useState(null);
  const [status, setStatus] = useState('typing');

  if (status === 'success') {
   return <h1>Porque tem "bois nos ares"!</h1>
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setStatus('submitting');
    try {
      await submitForm(answer);
      setStatus('success');
    } catch (err) {
      setStatus('typing');
      setError(err);
    }
  }

  function handleTextareaChange(e) {
    setAnswer(e.target.value);
  }

  return (
<div classNameName='container'>
    <Link to="/" classNameName='btn-voltar'>Voltar</Link>
    <div classNameName='div-test'>
      <h2>City quiz</h2>
      <p>
        Por que na argentina as vacas vivem olhando pro céu??
      </p>
      <form onSubmit={handleSubmit}>
        <textarea
          value={answer}
          onChange={handleTextareaChange}
          disabled={status === 'submitting'}
        />
        <br />
        <button classNameName='botao' disabled={
          answer.length === 0 ||
          status === 'submitting'
        }>
          Submit
        </button>
        {error !== null &&
          <p classNameName="Error">
            {error.message}
          </p>
        }
      </form>
      </div>
      </div>
  );
}

function submitForm(answer) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      let shouldError = answer.toLowerCase() !== 'buenos aires'
      if (shouldError) {
        reject(new Error('você errou, sinto muito!'));
      } else {
        resolve();
      }
    }, 1500);
  });
}
