import './card.css';
import Back from '../img/back.jpeg';

export default function SingleCard({ card, handleChoice, flipped, disabled }) {

    const handleClick = () => {
        if(!disabled) {
        handleChoice(card)
        }
    }

    return(
        <div className="card">
            <div className={flipped ? "flipped" : ""}> 
              <img className="front" src={card.src} alt='card front'/>
              <img 
               className="back"
               src={Back}
               onClick={handleClick}
               alt='card back'/>
            </div>
          </div>
    )
}
